import React, { Component } from 'react'
import Navigation from "../components/Navigation";
import { Card, Button } from "react-bootstrap";
import "./test.css"
const media  =window.matchMedia('(max-width:500px)')// key to make it respoonsive
export default class HomeScreen extends Component {
    constructor(props){
        super(props)
        this.state={
            jobspublished:1
        }
    }
    render() {
        console.log(media.matches)
        return (
            <div className ="maincontainer">
                <Navigation />
                <div className = "cardcontainer" style={{
                    display: 'flex',
                    flexDirection: `${media.matches?"column": "row"}`,
                    marginLeft: "1.2rem",
                    height: "100%",
                    width: "100%",
                    // "@media (":

                }}>
                    <div className  = "leftcardcontainer" style={{
                        width: `${media.matches?"100%": "75%"}`
                    }}>
                        <Card style={{
                            // width: "75%",
                            marginTop: 30,
                        }}>
                            <Card.Body>
                                <div>
                                    <Card.Title>
                                        <p style={{
                                            color: "red",
                                            display: 'inline-block',

                                        }}>
                                            Good Evening, Shankar!
                                        </p>
                                        <Button variant="primary" style={styles.postjobbutton()}>
                                            Post Job
                                        </Button>
                                    </Card.Title>
                                </div>


                                <div  style={
                                    styles.statscard()
                                }>
                                    <div style={
                                        styles.statsinfo()

                                    }>
                                        <p style={{ color: "red", fontSize: 30 }}>
                                            1
                                    </p>
                                        <p>Active jobs</p>
                                    </div>
                                    <div style={
                                        styles.statsinfo()
                                    }>
                                        <p style={{ color: "red", fontSize: 30 }}>
                                            1
                                    </p>
                                        <p>Active jobs</p>
                                    </div>

                                    <div style={styles.statsinfo()}>
                                        <p style={{ color: "red", fontSize: 30 }}>
                                            1
                                    </p>
                                        <p>Active jobs</p>
                                    </div>
                                    <div style={styles.statsinfo()}>
                                        <p style={{ color: "red", fontSize: 30 }}>
                                            1
                                    </p>
                                        <p>Active jobs</p>
                                    </div>
                                    <div style={styles.statsinfo()}>
                                        <p style={{ color: "red", fontSize: 30 }}>
                                            1
                                    </p>
                                        <p>Active jobs</p>
                                    </div>

                                </div>

                            </Card.Body>
                        </Card>
                        {/* </div> */}


                        <div className ="downcardcontainer" style={{
                            display:"flex",
                            flexDirection:'row'


                        }}>
                            <div style={{
                                width:"60%"
                            }}>
                                <p style={{
                                    marginTop:"2.5rem",
                                    // display: 'inline-block',
                                    fontWeight:"bold",
                                    color:"black"
                                }}> Overview of Active Jobs</p>
                                <Card style={{
                                    // width: "60%",
                                    // marginTop: 5,
                                }}>
                                    <Card.Body>
                                        <div>
                                            <Card.Title>
                                                <p style={{
                                                    color: "red",
                                                    display: 'inline-block'
                                                }}>Its your overview section</p>

                                            </Card.Title>
                                        </div>


                                        <Card.Text>
                                                <p>
                                                Its your overview section
                                                </p>
                                                <p>
                                                Its your overview section
                                                </p>
                                                <p>
                                                Its your overview section
                                                </p>
                                                <p>
                                                Its your overview section
                                                </p>
                                                <p>
                                                Its your overview section
                                                </p>
                                                <p>
                                                Its your overview section
                                                </p>

                                        </Card.Text>

                                    </Card.Body>
                                </Card>
                                <p style={{
                                    marginTop:"2.5rem",
                                    fontWeight:"bold",
                                    color:"black"
                                }}>
                                    Recent jobs
                                </p>
                                <Card>
                                    <Card.Title>
                                        <div style={{
                                            // backgroundColor:'black'
                                            // border:"1px solid"
                                            borderBottomWidth:"18px",
                                            borderBottomColor:"red"
                                        }}>
                                            <p style={{
                                                fontWeight:"bold",
                                                color:"#2a3252",
                                                fontSize:'1rem'
                                            }}>Jobs Published({this.state.jobspublished})</p>
                                        </div>

                                    </Card.Title>

                                </Card>
                                <p>
                                    hirinig for other roles
                                </p>
                                <Card>
                                    <p>
                                        recent jobs
                                    </p>
                                </Card>
                            </div>
                            <div style={{
                                marginLeft: "1.2rem",
                                width:"40%"
                            }}>
                                <p style={{
                                    marginTop:"2.5rem",
                                    display: 'inline-block'
                                }}> Upcoming Interviews</p>
                                <Card
                                    style={{


                                    }}
                                >
                                    <div>
                                    </div>
                                </Card>
                                <div>
                                    <p>Explore</p>
                                <div>
                                    last box
                                </div>
                            </div>
                        </div>

                        </div>
                    </div>
                    <div className="rightcardcontainer" style={{
                            width: `${media.matches?"100%": "25%"}`,
                            marginTop:"1.2rem",
                            marginLeft: "1.2rem",
                            marginRight:"0.7rem"
                        }}>

                            <div style={{
                                border: "1px solid white",
                                borderBottomColor: "black",
                                marginBottom: "1.2rem"

                            }}>
                                <h5>
                                    Your Chats
                            </h5>
                            </div>
                            <div style={{
                                border: "1px solid white",
                                borderBottomColor: "black",
                                marginBottom: "1.2rem"

                            }}>
                                <h5>
                                    Your Chats
                            </h5>
                            </div>
                            <div style={{
                                border: "1px solid white",
                                borderBottomColor: "black",
                                marginBottom: "1.2rem"

                            }}>
                                <h5>
                                    Your Chats
                            </h5>
                            </div>
                            <div style={{
                                border: "1px solid white",
                                borderBottomColor: "black",
                                marginBottom: "1.2rem"

                            }}>
                                <h5>
                                    Your Chats
                                </h5>
                            </div>
                            <div>
                            </div>
                            <div>

                        </div>
                        </div>
                        {/* </div> */}
                    </div> 
                </div>
            // </div>
        )
    }
}


const styles = {
    statscard(){
        if (media.matches ){
            return {
                display:"flex",
                marginLeft: "1rem",
                // marginLeft: "1rem",
                flexDirection: "column",


            }
        }else{
            return {

