import React, { Component } from 'react'

export default class AddUser extends Component {
    constructor(){
        super()
        this.state ={
            clicked_on_view_password :false,
            error_hai:true
        }
    }
    render() {
        return (
            <div style=
            {{
                margin:"1rem",
                display:'flex',
                flexDirection:"column"

            }}>

                <div style={{
                    textAlign:'center',
                    flex:1
                }}>
                    <p>
                        Add User
                    </p>

                </div>
                <div style={{
                    flex:1,
                    // backgroundColor:"red",
                    display:'flex',
                    flexDirection:'column'
                }}>
                    <form autoComplete="off">
                        <div style={styles.inputdiv}>
                            <input placeholder="Name" style={styles.input}/>
                            <p>Error</p>
                        </div>
                        <div style={styles.inputdiv}>
                            <input placeholder="Email"style={styles.input}/>
                            <p>Error</p>
                        </div>
                        <div style={{
                            flex:1,
                            margin:"1rem" ,
                            border:`${this.state.error_hai?"2 px solid red":"1px solid black"}`
                        }}>
                            <input type={this.state.clicked_on_view_password?"text":"password"} placeholder="Password"style={{
                                width:"20%",

                            }}/>
                            <p>Error</p>
                        </div>
                        <div style={styles.inputdiv}>
                            <input placeholder="confirm password"style={styles.passwordinput}/>
                            <p>Error</p>
                        </div>
                    </form>
                </div>
            </div>
        )
    }
}

const styles= {
    inputdiv:{
        flex:1,
        margin:"1rem"
    },
    input:{
        width:"80%"

    },
    passwordinput:{
        width:"20%"
    }
}
