import React, { Component } from 'react'
import Navigation from "../components/Navigation";
import { Card, Button } from "react-bootstrap";
import "./test.css"
const media  =window.matchMedia('(max-width:500px)')// key to make it respoonsive
export default class HomeScreen extends Component {
    render() {
        console.log(media.matches)
        return (
            <div className ="maincontainer">
                <Navigation />
                <div className = "cardcontainer" style={{
                    display: 'flex',
                    flexDirection: `${media.matches?"column": "row"}`,
                    marginLeft: "1.2rem",
                    height: "100%",
                    width: "100%",
                    // "@media (":

                }}>
                    <div className  = "leftcardcontainer" style={{
                        width: `${media.matches?"100%": "75%"}`
                    }}>
                        <Card style={{
                            // width: "75%",
                            marginTop: 30,
                        }}>
                            <Card.Body>
                                <div>
                                    <Card.Title>
                                        <p style={{
                                            color: "green",
                                            display: 'inline-block'
                                        }}>
                                            Good Evening, Shankar!
                                        </p>
                                        <Button variant="primary" style={styles.postjobbutton()}>
                                            Post Job
                                    </Button>
                                    </Card.Title>
                                </div>


                                <div  style={
                                    styles.statscard()
                                }>
                                    <div style={
                                        styles.statsinfo()

                                    }>
                                        <p style={{ color: "green", fontSize: 30 }}>
                                            1
                                    </p>
                                        <p>Active jobs</p>
                                    </div>
                                    <div style={
                                        styles.statsinfo()
                                    }>
                                        <p style={{ color: "green", fontSize: 30 }}>
                                            1
                                    </p>
                                        <p>Active jobs</p>
                                    </div>

                                    <div style={styles.statsinfo()}>
                                        <p style={{ color: "green", fontSize: 30 }}>
                                            1
                                    </p>
                                        <p>Active jobs</p>
                                    </div>
                                    <div style={styles.statsinfo()}>
                                        <p style={{ color: "green", fontSize: 30 }}>
                                            1
                                    </p>
                                        <p>Active jobs</p>
                                    </div>
                                    <div style={styles.statsinfo()}>
                                        <p style={{ color: "green", fontSize: 30 }}>
                                            1
                                    </p>
                                        <p>Active jobs</p>
                                    </div>

                                </div>

                            </Card.Body>
                        </Card>
                        {/* </div> */}


                        <div className ="downcardcontainer" style={{
                            display:"flex",
                            flexDirection:'row'


                        }}>
                            <div style={{
                                width:"60%"
                            }}>
                                <p style={{
                                    marginTop: 40,
                                    display: 'inline-block'
                                }}> Overview of Active Jobs</p>
                                <Card style={{
                                    // width: "60%",
                                    marginTop: 5,
                                }}>
                                    <Card.Body>
                                        <div>
                                            <Card.Title>
                                                <p style={{
                                                    color: "green",
                                                    display: 'inline-block'
                                                }}>Good Evening, Shankar!</p>
                                                <Button variant="primary" style={{
                                                    backgroundColor: "green",
                                                    float: "right"
                                                }}>
                                                    Post Job
                                        </Button>
                                            </Card.Title>
                                        </div>


                                        <Card.Text>

                                        </Card.Text>

                                    </Card.Body>
                                </Card>
                                <p>
                                    Recent jobs
                                </p>
                                <Card>
                                    <p>
                                        recent jobs
                                    </p>
                                </Card>
                                <p>
                                    hirinig for other roles
                                </p>
                                <Card>
                                    <p>
                                        recent jobs
                                    </p>
                                </Card>
                            </div>
                            <div style={{
                                marginLeft: 20,
                                width:"40%"
                            }}>
                                <p style={{
                                    marginTop: 40,
                                    display: 'inline-block'
                                }}> Upcoming Interviews</p>
                                <Card
                                    style={{


                                    }}
                                >
                                    <div>
                                    </div>
                                </Card>
                                <div>
                                    <p>Explore</p>
                                <div>
                                    last box
                                </div>
                            </div>
                        </div>

                        </div>
                    </div>
                    <div className="rightcardcontainer" style={{
                            width: `${media.matches?"100%": "25%"}`,
                            marginTop: 20,
                            marginLeft: 20,
                            marginRight: 10
                        }}>

                            <div style={{
                                border: "1px solid white",
                                borderBottomColor: "black",
                                marginBottom: 20

                            }}>
                                <h5>
                                    Your Chats
                            </h5>
                            </div>
                            <div style={{
                                border: "1px solid white",
                                borderBottomColor: "black",
                                marginBottom: 20

                            }}>
                                <h5>
                                    Your Chats
                            </h5>
                            </div>
                            <div style={{
                                border: "1px solid white",
                                borderBottomColor: "black",
                                marginBottom: 20

                            }}>
                                <h5>
                                    Your Chats
                            </h5>
                            </div>
                            <div style={{
                                border: "1px solid white",
                                borderBottomColor: "black",
                                marginBottom: 20

                            }}>
                                <h5>
                                    Your Chats
                                </h5>
                            </div>
                            <div>
                            </div>
                            <div>

                        </div>
                        </div>
                        {/* </div> */}
                    </div>
                </div>
            // </div>
        )
    }
}


const styles = {
    statscard(){
        if (media.matches ){
            return {
                display:"flex",
                marginLeft: "1rem",
                // marginLeft: "1rem",
                flexDirection: "column",


            }
        }else{
            return {

                    marginLeft: "1rem",
                    flexDirection: "row",
                    display: 'flex',
                }
        }
    },
    statsinfo(){
        if (media.matches){
            return{
                flex:1/2,
                border: "1px solid white",
                borderRightColor: "black",
                borderBottomColor:"black",
                textAlign: 'center',
                alignItems: "center"
            }
        }else{
            return{
                flex:1,
                border: "1px solid white",
                borderRightColor: "black",
                // borderBottomColor:"black",
                textAlign: 'center',
                alignItems: "center"
            }
        }
    },
    postjobbutton(){
        if (media.matches){
            return{
            backgroundColor: "green",
            float: "left"
            }
        }else{
            return{
                backgroundColor: "green",
                float: "right"
                }
        }
    }
}
